<?session_start();?><!DOCTYPE HTML><script>	window.fbAsyncInit = function() {	FB.init({	  appId      : '1608846309411850',	  xfbml      : true,	  version    : 'v2.8'	});			};	(function(d, s, id){		var js, fjs = d.getElementsByTagName(s)[0];		if (d.getElementById(id)) {return;}		js = d.createElement(s); js.id = id;		js.src = "//connect.facebook.net/en_US/sdk.js";		fjs.parentNode.insertBefore(js, fjs);	}(document, 'script', 'facebook-jssdk'));			function login(){				FB.getLoginStatus(function(response) {			if (response.status === 'connected') {					getInfo();								} else if (response.status === 'not_authorized') {				$('#status').html("Log in failed");			} else {				$('#status').html("Please Log-in");			}		},{			scope: 'email',			status: 'connected',			authResponse: {				accessToken: '...',				expiresIn:'...',				signedRequest:'...',				userID:'...'			}});	}	function getInfo() {					FB.api('/me', 'GET', {fields: 'first_name,last_name,name,id,email'}, function(response) {			loadInfo(response);				});			}		function loadInfo(response){		var userresult;		$.ajax({url: "session_start.php?id="+response.id});		$.ajax({url: "loggedin.php?query=checkuser&&id="+response.id, success: function(result){			if(result == "true")			{				$(location).attr('href', 'http://strikesparks.com/userhome.php');			}			else			{				$.ajax({url: "loggedin.php?query=createuser&&id="+response.id+"&&firstname="+response.first_name+"&&lastname="+response.last_name+"&&email="+response.email, success: function(result){				$(location).attr('href', 'http://strikesparks.com/userhome.php');								}});							}		}});	 	}	</script><html>	<head>		<? include 'includes.html'; ?>		<title>Spark</title>		<meta charset="utf-8" />		<meta name="viewport" content="width=device-width, initial-scale=1" />	</head>	<body>		<div id="wrapper">		<!-- Header -->		<? include 'header.html'; ?>		<div id="status"></div>		<button onclick="login()" id="login">Login with Facebook</button></div>		<!-- Scripts -->	</body></html>